import{_ as e,c as a,a2 as r,o as l}from"./chunks/framework.BMFc6z2H.js";const x=JSON.parse('{"title":"小六壬","description":"","frontmatter":{"title":"小六壬"},"headers":[],"relativePath":"卜/小六壬.md","filePath":"卜/小六壬.md","lastUpdated":1732612931000}'),n={name:"卜/小六壬.md"};function d(i,t,p,h,s,o){return l(),a("div",null,t[0]||(t[0]=[r('<h2 id="小六壬对照表" tabindex="-1">小六壬对照表 <a class="header-anchor" href="#小六壬对照表" aria-label="Permalink to &quot;小六壬对照表&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:center;"></th><th style="text-align:center;">大安</th><th style="text-align:center;">留连</th><th style="text-align:center;">速喜</th><th style="text-align:center;">赤口</th><th style="text-align:center;">小吉</th><th style="text-align:center;">空亡</th></tr></thead><tbody><tr><td style="text-align:center;"><strong>五行</strong></td><td style="text-align:center;">木</td><td style="text-align:center;">水</td><td style="text-align:center;">火</td><td style="text-align:center;">金</td><td style="text-align:center;">木</td><td style="text-align:center;">土</td></tr><tr><td style="text-align:center;"><strong>方位</strong></td><td style="text-align:center;">东方</td><td style="text-align:center;">北方</td><td style="text-align:center;">南方</td><td style="text-align:center;">西方</td><td style="text-align:center;">东方</td><td style="text-align:center;">四面八方</td></tr><tr><td style="text-align:center;"><strong>六兽</strong></td><td style="text-align:center;">青龙</td><td style="text-align:center;">玄武</td><td style="text-align:center;">朱雀</td><td style="text-align:center;">白虎</td><td style="text-align:center;">六合</td><td style="text-align:center;">勾陈</td></tr><tr><td style="text-align:center;"><strong>谋事</strong></td><td style="text-align:center;">一 五 七</td><td style="text-align:center;">六 十</td><td style="text-align:center;">三 六 九</td><td style="text-align:center;">四 七 十</td><td style="text-align:center;">二 五 八</td><td style="text-align:center;">三 六 九</td></tr></tbody></table><h2 id="小六壬速断口诀" tabindex="-1">小六壬速断口诀 <a class="header-anchor" href="#小六壬速断口诀" aria-label="Permalink to &quot;小六壬速断口诀&quot;">​</a></h2><h3 id="大安" tabindex="-1">大安 <a class="header-anchor" href="#大安" aria-label="Permalink to &quot;大安&quot;">​</a></h3><p>大安事安昌 求财在坤方 失物去不远 宅舍保安康</p><p>行人身未动 病者主无妨 将军回田野 仔细更推详</p><h3 id="留连" tabindex="-1">留连 <a class="header-anchor" href="#留连" aria-label="Permalink to &quot;留连&quot;">​</a></h3><p>留连事难成 求谋日未明 官事只宜缓 去者未回程</p><p>失物南方见 急讨方趁心 更须防口舌 人口只平平</p><h3 id="速喜" tabindex="-1">速喜 <a class="header-anchor" href="#速喜" aria-label="Permalink to &quot;速喜&quot;">​</a></h3><p>速喜喜来临 求财向南行 失物申午未 逢人路上寻</p><p>官事有福德 病者无祸侵 田宅六蓄吉 行人有信音</p><h3 id="赤口" tabindex="-1">赤口 <a class="header-anchor" href="#赤口" aria-label="Permalink to &quot;赤口&quot;">​</a></h3><p>赤口主口舌 官非切要防 失物速速讨 行人有惊慌</p><p>六畜多作怪 病者出西方 更须防诅咒 诚恐染瘟肓</p><h3 id="小吉" tabindex="-1">小吉 <a class="header-anchor" href="#小吉" aria-label="Permalink to &quot;小吉&quot;">​</a></h3><p>小吉最吉昌 路上好商量 阴人来报喜 失物在坤方</p><p>行人即便至 交关甚是强 凡事街合和 病者叩穹苍</p><h3 id="空亡" tabindex="-1">空亡 <a class="header-anchor" href="#空亡" aria-label="Permalink to &quot;空亡&quot;">​</a></h3><p>空亡事不祥 阴人少乖张 求财无利益 行人有灾殃</p><p>失物寻不见 官事有刑伤 病人逢暗鬼 解禳保平安</p><h2 id="日加时断口诀" tabindex="-1">日加时断口诀 <a class="header-anchor" href="#日加时断口诀" aria-label="Permalink to &quot;日加时断口诀&quot;">​</a></h2><h3 id="大安-1" tabindex="-1">大安 <a class="header-anchor" href="#大安-1" aria-label="Permalink to &quot;大安&quot;">​</a></h3><p>大安加留连，办事不周全，失物西北去，婚姻晚几天。</p><p>大安加速喜，事事自己起，失物当日见，婚姻自己提。</p><p>大安加赤口，办事不顺手，失物不用找，婚姻两分手。</p><p>大安加小吉，事事从己及，失物不出门，婚姻成就地。</p><p>大安加空亡，病人要上床，失物无踪影，事事不顺情。</p><h3 id="留连-1" tabindex="-1">留连 <a class="header-anchor" href="#留连-1" aria-label="Permalink to &quot;留连&quot;">​</a></h3><p>留连加大安，办事两分张，婚姻有喜事，先苦后来甜。</p><p>留连加速喜，事事由自己，婚姻有成意，失物三天里。</p><p>留连加赤口，病者死人口，失物准丢失，婚姻两分手。</p><p>留连加小吉，事事不用提，失物东南去，病者出人齐。</p><p>留连加空亡，病人准死亡，失物不见面，婚姻两分张。</p><h3 id="速喜-1" tabindex="-1">速喜 <a class="header-anchor" href="#速喜-1" aria-label="Permalink to &quot;速喜&quot;">​</a></h3><p>速喜加赤口，自己往外走，失物往正北，婚姻得勤走。</p><p>速喜加小吉，婚姻有人提，病人当天好，时物在家里。</p><p>速喜加空亡，婚姻有分张，病者积极治，失物不久见。</p><p>速喜加大安，事事都平安，姻姻成全了，占病都相安。</p><p>速喜加留连，婚姻不可言，失物无信息，病人有仙缘。</p><h3 id="赤口-1" tabindex="-1">赤口 <a class="header-anchor" href="#赤口-1" aria-label="Permalink to &quot;赤口&quot;">​</a></h3><p>赤口加小吉，办事自己提，婚姻不能成，失物无信息。</p><p>赤口加空亡，无病也上床，失物不用找，婚姻不能成。</p><p>赤口加大安，办事险和难，失物东北找，婚姻指定难。</p><p>赤口加留连，办事有困难，行人在外走，失物不回还。</p><p>赤口加速喜，婚姻在自己，失物有着落，办事官事起。</p><h3 id="小吉-1" tabindex="-1">小吉 <a class="header-anchor" href="#小吉-1" aria-label="Permalink to &quot;小吉&quot;">​</a></h3><p>小吉加空亡，病人不妥当，失物正东找，婚姻再想想。</p><p>小吉加大安，事事两周全，婚姻当日定，失物自己损。</p><p>小吉加留连，事事有反还，婚姻有人破，失物上西南。</p><p>小吉加速喜，事事从头起，婚姻能成就，失物在院里。</p><p>小吉加赤口，办事往外走，婚姻有难处，失物丢了手。</p><h3 id="空亡-1" tabindex="-1">空亡 <a class="header-anchor" href="#空亡-1" aria-label="Permalink to &quot;空亡&quot;">​</a></h3><p>空亡加大安，事事不周全，婚姻从和好，失物反复间。</p><p>空亡加留连，办事处处难，婚姻重新定，失物永不还。</p><p>空亡加速喜，事事怨自己，婚姻有一定，失物在家里。</p><p>空亡加赤口，办事官非有，婚姻难定准，失物往远走。</p><p>空亡加小吉，事事有猜疑，婚姻有喜事，失物回家里。</p><p>以上为月上起日所落之宫位，加日上起时所落之宫位，据二宫位关系断其结果。</p>',59)]))}const g=e(n,[["render",d]]);export{x as __pageData,g as default};
