import{_ as t,c as o,a2 as s,o as i}from"./chunks/framework.BMFc6z2H.js";const h=JSON.parse('{"title":"周易六十四卦","description":"","frontmatter":{"title":"周易六十四卦"},"headers":[],"relativePath":"相关经典/周易六十四卦.md","filePath":"相关经典/周易六十四卦.md","lastUpdated":1732631266000}'),r={name:"相关经典/周易六十四卦.md"};function n(g,l,a,c,u,e){return i(),o("div",null,l[0]||(l[0]=[s('<h2 id="易经-64-卦" tabindex="-1">易经 64 卦 <a class="header-anchor" href="#易经-64-卦" aria-label="Permalink to &quot;易经 64 卦&quot;">​</a></h2><table tabindex="0"><thead><tr><th></th><th><strong>☰ 天</strong></th><th><strong>☱ 泽</strong></th><th><strong>☲ 火</strong></th><th><strong>☳ 雷</strong></th><th><strong>☴ 风</strong></th><th><strong>☵ 水</strong></th><th><strong>☶ 山</strong></th><th><strong>☷ 地</strong></th></tr></thead><tbody><tr><td><strong>☰ 天</strong></td><td>䷀ 乾</td><td>䷪ 夬</td><td>䷍ 大有</td><td>䷡ 大壮</td><td>䷈ 小畜</td><td>䷄ 需</td><td>䷙ 大畜</td><td>䷊ 泰</td></tr><tr><td><strong>☱ 泽</strong></td><td>䷉ 履</td><td>䷹ 兑</td><td>䷥ 睽</td><td>䷵ 归妹</td><td>䷼ 中孚</td><td>䷻ 节</td><td>䷨ 损</td><td>䷒ 临</td></tr><tr><td><strong>☲ 火</strong></td><td>䷌ 同人</td><td>䷰ 革</td><td>䷝ 离</td><td>䷶ 丰</td><td>䷤ 家人</td><td>䷾ 既济</td><td>䷕ 贲</td><td>䷣ 明夷</td></tr><tr><td><strong>☳ 雷</strong></td><td>䷘ 无妄</td><td>䷐ 随</td><td>䷔ 噬嗑</td><td>䷲ 震</td><td>䷩ 益</td><td>䷂ 屯</td><td>䷚ 颐</td><td>䷗ 复</td></tr><tr><td><strong>☴ 风</strong></td><td>䷫ 姤</td><td>䷛ 大过</td><td>䷱ 鼎</td><td>䷟ 恒</td><td>䷸ 巽</td><td>䷯ 井</td><td>䷑ 蛊</td><td>䷭ 升</td></tr><tr><td><strong>☵ 水</strong></td><td>䷅ 讼</td><td>䷮ 困</td><td>䷿ 未济</td><td>䷧ 解</td><td>䷺ 涣</td><td>䷜ 坎</td><td>䷃ 蒙</td><td>䷆ 师</td></tr><tr><td><strong>☶ 山</strong></td><td>䷠ 遁</td><td>䷞ 咸</td><td>䷷ 旅</td><td>䷽ 小过</td><td>䷴ 渐</td><td>䷦ 蹇</td><td>䷳ 艮</td><td>䷎ 谦</td></tr><tr><td><strong>☷ 地</strong></td><td>䷋ 否</td><td>䷬ 萃</td><td>䷢ 晋</td><td>䷏ 豫</td><td>䷓ 观</td><td>䷇ 比</td><td>䷖ 剥</td><td>䷁ 坤</td></tr></tbody></table><h2 id="分宫卦象次序歌" tabindex="-1">分宫卦象次序歌 <a class="header-anchor" href="#分宫卦象次序歌" aria-label="Permalink to &quot;分宫卦象次序歌&quot;">​</a></h2><ul><li>乾为天，天风姤，天山遁，天地否，风地观，山地剥，火地晋，火天大有。</li><li>坎为水，水泽节，水雷屯，水火既济，泽火革，雷火丰，地火明夷，地水师。</li><li>艮为山，山火贲．山天大畜，山泽损，火泽睽，天泽履，风泽中孚，风山渐。</li><li>震为雷，雷地豫，雷水解，雷风恒，地风升，水风井，泽风大过，泽雷随。</li><li>巽为风，风天小畜，风火家人，风雷益，天雷无妄，火雷噬嗑，山雷颐，山风蛊。</li><li>离为火，火山旅，火风鼎，火水未济，山水蒙，风水涣，天水讼，天火同人。</li><li>坤为地，地雷复，地泽临，地天泰，雷天大壮，泽天夬，水天需，水地比。</li><li>兑为泽，泽水困，泽地萃，泽山咸，水山蹇，地山谦，雷山小过，雷泽归妹。</li></ul><h2 id="第一卦-乾" tabindex="-1">第一卦 乾 <a class="header-anchor" href="#第一卦-乾" aria-label="Permalink to &quot;第一卦 乾&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">乾为天 乾上乾下</p><ul><li><strong>乾</strong>：元，亨，利，贞。</li><li><strong>彖曰</strong>：大哉乾元，万物资始，乃统天。云行雨施，品物流形。大明始终，六位时成，时乘六龙以御天。乾道变化，各正性命，保合大和，乃利贞。首出庶物，万国咸宁。</li><li><strong>象曰</strong>：天行健，君子以自强不息。潜龙勿用，阳在下也。见龙再田，德施普也。终日乾乾，反复道也。或跃在渊，进无咎也。飞龙在天，大人造也。亢龙有悔，盈不可久也。用 九，天德不可为首也。</li></ul></div><ul><li><strong>初九</strong>：潜龙，勿用。</li><li><strong>九二</strong>：见龙再田，利见大人。</li><li><strong>九三</strong>：君子终日乾乾，夕惕若，厉无咎。</li><li><strong>九四</strong>：或跃在渊，无咎。九五：飞龙在天，利见大人。</li><li><strong>九五</strong>：飞龙在天，利见大人。</li><li><strong>上九</strong>：亢龙有悔。用九：见群龙 无首，吉。</li></ul><h2 id="第二卦-坤" tabindex="-1">第二卦 坤 <a class="header-anchor" href="#第二卦-坤" aria-label="Permalink to &quot;第二卦 坤&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">坤为地 坤上坤下</p><ul><li><strong>坤</strong>：元，亨，利牝马之贞。君子有攸往，先迷后得主，利西南得朋，东北丧朋。安贞，吉。</li><li><strong>彖曰</strong>：至哉坤元，万物资生，乃顺承天。坤厚载物，德合无疆。含弘光大，品物咸亨。牝马地类，行地无疆，柔顺利贞。君子攸行，先迷失道，后顺得常。西南得朋，乃与类行；东北丧朋，乃终有庆。安贞之吉，应地无疆。</li><li><strong>象曰</strong>：地势坤，君子以厚德载物。</li></ul></div><ul><li><strong>初六</strong>：履霜，坚冰至。象曰：履霜坚冰，阴始凝也。驯致其道，至坚冰也。</li><li><strong>六二</strong>：直，方，大，不习无不利。象曰：六二之动，直以方也。不习无不利，地道光也。</li><li><strong>六三</strong>：含章可贞。或从王事，无成有终。象曰：含章可贞；以时发也。或从王事，知光大也。</li><li><strong>六四</strong>：括囊；无咎，无誉。象曰：括囊无咎，慎不害也。</li><li><strong>六五</strong>：黄裳，元吉。象曰：黄裳元吉，文在中也。</li><li><strong>上六</strong>：战龙於野，其血玄黄。象曰：战龙於野，其道穷也。</li></ul><h2 id="第三卦-屯" tabindex="-1">第三卦 屯 <a class="header-anchor" href="#第三卦-屯" aria-label="Permalink to &quot;第三卦 屯&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">水雷屯 坎上震下</p><ul><li><strong>屯</strong>：元，亨，利，贞，勿用，有攸往，利建侯。</li><li><strong>彖曰</strong>：屯，刚柔始交而难生，动乎险中，大亨贞。雷雨之动满盈，天造草昧，宜建侯而不宁。</li><li><strong>象曰</strong>：云，雷，屯；君子以经纶。</li></ul></div><ul><li><strong>初九</strong>：磐桓；利居贞，利建侯。象曰：虽磐桓，志 行正也。以贵下贱，大得民也。</li><li><strong>六二</strong>：屯如(辶颤)如，乘马班如。匪寇婚媾，女子贞不字，十年乃字。象曰：六二之难，乘 刚也。十年乃字，反常也。</li><li><strong>六三</strong>：既鹿无虞，惟入于林中，君子几不如舍，往吝。象曰：既鹿无虞，以纵禽也。君子舍之，往吝穷也。</li><li><strong>六四</strong>：乘马班如，求婚媾，无不利。象曰：求而往，明也。</li><li><strong>九五</strong>：屯其膏，小贞吉，大贞凶。象曰：屯其膏，施未光也。</li><li><strong>上六</strong>：乘马班如，泣血涟如。象曰：泣血涟如，何可长也。</li></ul><h2 id="第四卦-蒙" tabindex="-1">第四卦 蒙 <a class="header-anchor" href="#第四卦-蒙" aria-label="Permalink to &quot;第四卦 蒙&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">山水蒙 艮上坎下</p><ul><li><strong>蒙</strong>：亨。匪我求童蒙，童蒙求我。初噬告，再三渎，渎则不告。利贞。</li><li><strong>彖曰</strong>：蒙，山下有险，险而止，蒙。蒙亨，以亨行时中也。匪我求童蒙，童蒙求我，志应也。初噬告，以刚中也。再三渎，渎则不告，渎蒙也。蒙以养正，圣功也。</li><li><strong>象曰</strong>：山下出 泉，蒙；君子以果行育德。</li></ul></div><ul><li><strong>初六</strong>：发蒙，利用刑人，用说桎梏，以往吝。象曰：利用刑人，以正法也。</li><li><strong>九二</strong>：包蒙吉；纳妇吉；子克家。象曰：子克家，刚柔接也。</li><li><strong>六三</strong>：勿用娶女；见金夫，不有躬，无攸利。象曰：勿用娶女，行不顺也。</li><li><strong>六四</strong>：困蒙，吝。象曰：困蒙之吝，独远实也。</li><li><strong>六五</strong>：童蒙，吉。象曰：童蒙之吉，顺以巽也。</li><li><strong>上九</strong>：击蒙；不利为寇，利御寇。象曰：利用御寇，上下顺也。</li></ul><h2 id="第五卦-需" tabindex="-1">第五卦 需 <a class="header-anchor" href="#第五卦-需" aria-label="Permalink to &quot;第五卦 需&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">水天需 坎上乾下</p><ul><li><strong>需</strong>：有孚，光亨，贞吉。利涉大川。</li><li><strong>彖曰</strong>：需，须也；险在前也。刚健而不陷，其义不困穷矣。需有孚，光亨，贞吉。位乎天位，以正中也。利涉大川，往有功也。</li><li><strong>象曰</strong>：云上於天，需；君子以饮食宴乐。</li></ul></div><ul><li><strong>初九</strong>：需 于郊。利用恒，无咎。象曰：需于郊，不犯难行也。利用恒，无咎；未失常也。</li><li><strong>九二</strong>：需于沙。小有言，终吉。象曰：需于沙，衍在中也。虽小有言，以终吉也。</li><li><strong>九三</strong>：需于泥，致寇至。象曰：需于泥，灾在外也。自我致寇，敬慎不败也。</li><li><strong>六四</strong>：需于血，出自穴。象曰：需于血，顺以听也。</li><li><strong>九五</strong>：需于酒食，贞吉。象曰：酒食贞吉，以中正也。</li><li><strong>上六</strong>：入于穴，有不速之客三人 来，敬之终吉。象曰：不速之客来，敬之终吉。虽不当位，未大失也。</li></ul><h2 id="第六卦-讼" tabindex="-1">第六卦 讼 <a class="header-anchor" href="#第六卦-讼" aria-label="Permalink to &quot;第六卦 讼&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">天水讼 乾上坎下</p><ul><li><strong>讼</strong>：有孚，窒。惕中吉。终凶。利见大人，不利涉大川。</li><li><strong>彖曰</strong>：讼，上刚下险，险而健讼。讼有孚窒，惕中吉，刚来而得中也。终凶；讼不可成也。利见大人；尚中正也。不利涉大川；入于渊也。</li><li><strong>象曰</strong>：天与水违行，讼；君子以作事谋始。</li></ul></div><ul><li><strong>初六</strong>：不永所事，小有言，终吉。象曰：不 永所事，讼不可长也。虽有小言，其辩明也。</li><li><strong>九二</strong>：不克讼，归而逋，其邑人三百户，无眚。象曰：不克讼，归而逋也。自下讼上，患至掇也。</li><li><strong>六三</strong>：食旧德，贞厉，终吉，或从王事，无成。象曰：食旧德，从上吉也。</li><li><strong>九四</strong>：不克讼，复自命，渝安贞，吉。象曰：复即命，渝安贞；不失也。</li><li><strong>九五</strong>：讼元吉。象曰：讼元吉，以中正也。</li><li><strong>上九</strong>：或锡之鞶带，终朝三褫之。象曰：以讼受服，亦不足敬也。</li></ul><h2 id="第七卦-师" tabindex="-1">第七卦 师 <a class="header-anchor" href="#第七卦-师" aria-label="Permalink to &quot;第七卦 师&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">地水师 坤上坎下</p><ul><li><strong>师</strong>：贞，丈人，吉无咎。</li><li><strong>彖曰</strong>：师，众也，贞正也，能以众正，可以王矣。刚中而应，行险而顺，以此毒天下，而民从之，吉又何咎矣。</li><li><strong>象曰</strong>：地中有水，师；君子以容民畜众。</li></ul></div><ul><li><strong>初六</strong>：师出以律，否臧凶。象曰：师出以律， 失律凶也。</li><li><strong>九二</strong>：在师中，吉无咎，王三锡命。象曰：在师中吉，承天宠也。王三锡命，怀万邦也。</li><li><strong>六三</strong>：师或舆尸，凶。象曰：师或舆尸，大无功也。</li><li><strong>六四</strong>：师左次，无咎。象曰：左次无咎，未失常也。</li><li><strong>六五</strong>：田有禽，利执言，无咎。长子帅师，弟子舆尸，贞凶。象曰：长子帅师，以中行也。弟子舆师，使不当也。</li><li><strong>上六</strong>：大君有命，开国承家，小人勿用。象曰：大君有命，以正功也。小人勿用，必乱邦也。</li></ul><h2 id="第八卦-比" tabindex="-1">第八卦 比 <a class="header-anchor" href="#第八卦-比" aria-label="Permalink to &quot;第八卦 比&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">水地比 坎上坤下</p><ul><li><strong>比</strong>：吉。原筮元永贞，无咎。不宁方来，后夫凶。</li><li><strong>彖曰</strong>：比，吉也，比，辅也，下顺从也。原筮元永贞，无咎，以刚中也。不宁方来，上下应也。后夫凶，其道穷也。</li><li><strong>象曰</strong>：地上有水，比；先王以建万国，亲诸侯。</li></ul></div><ul><li><strong>初六</strong>：有孚比之，无咎。有孚盈缶，终 来有他，吉。象曰：比之初六，有他吉也。</li><li><strong>六二</strong>：比自内，贞吉。象曰：比之自内，不自失也。</li><li><strong>六三</strong>：比之匪人。象曰：比之匪人，不亦伤乎！</li><li><strong>六四</strong>：外比之，贞吉。象曰：外比於贤，以从上也。</li><li><strong>九五</strong>：显比，王用三驱，失前禽。邑人不诫，吉。象曰：显比之吉，位正中也。舍逆取顺，失前禽也。邑人不诫，上使中也。</li><li><strong>上六</strong>：比之无首，凶。象曰：比之无首，无所终也。</li></ul><h2 id="第九卦-小畜" tabindex="-1">第九卦 小畜 <a class="header-anchor" href="#第九卦-小畜" aria-label="Permalink to &quot;第九卦 小畜&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">风天小畜 巽上乾下</p><ul><li><strong>小畜</strong>：亨。密云不雨，自我西郊。</li><li><strong>彖曰</strong>：小畜；柔得位，而上下应之，曰小畜。健而巽，刚中而志行，乃亨。密云不雨，尚往也。自我西郊，施未行也。</li><li><strong>象曰</strong>：风行天上，小畜；君子以懿文德。</li></ul></div><ul><li><strong>初九</strong>：复自道，何其咎，吉。象曰：复自道， 其义吉也。</li><li><strong>九二</strong>：牵复，吉。象曰：牵复在中，亦不自失也。</li><li><strong>九三</strong>：舆说辐，夫妻反目。象曰：夫 妻反目，不能正室也。</li><li><strong>六四</strong>：有孚，血去惕出，无咎。象曰：有孚惕出，上合志也。</li><li><strong>九五</strong>：有孚挛如，富以其邻。象曰：有孚挛如，不独富也。</li><li><strong>上九</strong>：既雨既处，尚德载，妇贞厉。月几望，君子征凶。象曰：既雨既处，德积载也。君子征凶，有所疑也。</li></ul><h2 id="第十卦-履" tabindex="-1">第十卦 履 <a class="header-anchor" href="#第十卦-履" aria-label="Permalink to &quot;第十卦 履&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">天泽履 乾上兑下</p><ul><li><strong>履</strong>：履虎尾，不咥人，亨。</li><li><strong>彖曰</strong>：履，柔履刚也。说而应乎乾，是以履虎尾，不咥人，亨。刚中正，履帝位而不疚，光明也。</li><li><strong>象曰</strong>：上天下泽，履；君子以辨上下，安民志。</li></ul></div><ul><li><strong>初九</strong>：素履，往无咎。象曰：素履之 往，独行愿也。</li><li><strong>九二</strong>：履道坦坦，幽人贞吉。象曰：幽人贞吉，中不自乱也。</li><li><strong>六三</strong>：眇能视，跛能履，履虎尾，咥人，凶。武人为于大君。象曰：眇能视；不足以有明也。跛能履；不足以与行也。咥人之凶；位不当也。武人为于大君；志刚也。</li><li><strong>九四</strong>：履虎尾，愬愬终吉。象曰：愬愬终吉，志行也。</li><li><strong>九五</strong>：夬履，贞厉。象曰：夬履贞厉，位正当也。</li><li><strong>上九</strong>：视履考祥，其旋元吉。象曰：元吉在上，大有庆也。</li></ul><h2 id="第十一卦-泰" tabindex="-1">第十一卦 泰 <a class="header-anchor" href="#第十一卦-泰" aria-label="Permalink to &quot;第十一卦 泰&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">天地泰 坤上乾下</p><ul><li><strong>泰</strong>：小往大来，吉亨。</li><li><strong>彖曰</strong>：泰，小往大来，吉亨。则是天地交，而万物通也；上下交，而其志同也。内阳而外阴，内健而外顺，内君子而外小人，君子道长，小人道消也。</li><li><strong>象曰</strong>：天地交泰，后以财（裁）成天地之道，辅相天地之宜，以左右民。</li></ul></div><ul><li><strong>初九</strong>：拔茅 茹，以其夤，征吉。象曰：拔茅征吉，志在外也。</li><li><strong>九二</strong>：包荒，用冯河，不遐遗，朋亡，得尚于中行。象曰：包荒，得尚于中行，以光大也。</li><li><strong>九三</strong>：无平不陂，无往不复，艰贞无咎。勿恤其孚，于食有福。象曰：无往不复，天地际也。</li><li><strong>六四</strong>：翩翩不富，以其邻，不戒以孚。象曰：翩翩不富，皆失实也。不戒以孚，中心愿也。</li><li><strong>六五</strong>：帝乙归妹，以祉元吉。象曰：以祉元吉，中以行愿也。</li><li><strong>上六</strong>：城复于隍，勿用师。自邑告命，贞吝。象曰：城复于隍，其命乱也。</li></ul><h2 id="第十二卦-否" tabindex="-1">第十二卦 否 <a class="header-anchor" href="#第十二卦-否" aria-label="Permalink to &quot;第十二卦 否&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">地天否 乾上坤下</p><ul><li><strong>否</strong>：否之匪人，不利君子贞，大往小来。</li><li><strong>彖曰</strong>：否之匪人，不利君子贞。大往小来，则是天地不交，而万物不通也；上下不交，而天下无邦也。内阴而外阳，内柔而外刚，内小人而外君子。小人道长，君子道消也。</li><li><strong>象曰</strong>：天地不交，否；君子以俭德辟难，不可荣以禄。</li></ul></div><ul><li><strong>初六</strong>：拔茅茹，以其夤，贞吉 亨。象曰：拔茅贞吉，志在君也。</li><li><strong>六二</strong>：包承。小人吉，大人否亨。象曰：大人否亨，不乱群也。</li><li><strong>六三</strong>：包羞。象曰：包羞，位不当也。</li><li><strong>九四</strong>：有命无咎，畴离祉。象曰：有命无咎，志行也。</li><li><strong>九五</strong>：休否，大人吉。其亡其亡，系于苞桑。象曰：大人之吉，位正当也。</li><li><strong>上九</strong>：倾否，先否后喜。象曰：否终则倾，何可长也。</li></ul><h2 id="第十三卦-同人" tabindex="-1">第十三卦 同人 <a class="header-anchor" href="#第十三卦-同人" aria-label="Permalink to &quot;第十三卦 同人&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">天火同人 乾上离下</p><ul><li><strong>同人</strong>：同人于野，亨。利涉大川，利君子贞。</li><li><strong>彖曰</strong>：同人，柔得位得中，而应乎乾，曰同人。同人曰，同人于野，亨。利涉大川，乾行也。文明以健，中正而应，君子正也。唯君子为能通天下之志。</li><li><strong>象曰</strong>：天与火，同人；君子以类族辨物。</li></ul></div><ul><li><strong>初九</strong>：同人于门，无咎。象曰：出门同人，又 谁咎也。</li><li><strong>六二</strong>：同人于宗，吝。象曰：同人于宗，吝道也。</li><li><strong>九三</strong>：伏戎于莽，升其高陵，三岁不兴。象曰：伏戎于莽，敌刚也。三岁不兴，安行也。</li><li><strong>九四</strong>：乘其墉，弗克攻，吉。象曰：乘其墉，义弗克也，其吉，则困而反则也。</li><li><strong>九五</strong>：同人，先号啕而后笑。大师克相遇。象曰：同人之先，以中直也。大师相遇，言相克也。</li><li><strong>上九</strong>：同人于郊，无悔。象曰：同人于郊，志未得也。</li></ul><h2 id="第十四卦-大有" tabindex="-1">第十四卦 大有 <a class="header-anchor" href="#第十四卦-大有" aria-label="Permalink to &quot;第十四卦 大有&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">火天大有 离上乾下</p><ul><li><strong>大有</strong>：元亨。</li><li><strong>彖曰</strong>：大有，柔得尊位，大中而上下应之，曰大有。其德刚健而文明，应乎天而时行，是以元亨。</li><li><strong>象曰</strong>：火在天上，大有；君子以竭恶扬善，顺天休命。</li></ul></div><ul><li><strong>初九</strong>：无交害，匪咎，艰则无 咎。象曰：大有初九，无交害也。</li><li><strong>九二</strong>：大车以载，有攸往，无咎。象曰：大车以载，积中不败也。</li><li><strong>九三</strong>：公用亨于天子，小人弗克。象曰：公用亨于天子，小人害也。九四：匪其彭，无咎。象曰：匪其彭，无咎；明辨晰也。</li><li><strong>六五</strong>：厥孚交如，威如；吉。象曰：厥孚交如，信以发志也。威如之吉，易而无备也。</li><li><strong>上九</strong>：自天佑之，吉无不利。象曰：大有上吉，自天佑也。</li></ul><h2 id="第十五卦-谦" tabindex="-1">第十五卦 谦 <a class="header-anchor" href="#第十五卦-谦" aria-label="Permalink to &quot;第十五卦 谦&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">地山谦 坤上艮下</p><ul><li><strong>谦</strong>：亨，君子有终。</li><li><strong>彖曰</strong>：谦，亨，天道下济而光明，地道卑而上行。天道亏盈而益谦，地道变盈而流谦，鬼神害盈而福谦，人道恶盈而好谦。谦尊而光，卑而不可逾，君子之终也。</li><li><strong>象曰</strong>：地中有山，谦；君子以裒多益寡，称物平施。</li></ul></div><ul><li><strong>初六</strong>：谦谦君子，用涉大川，吉。 象曰：谦谦君子，卑以自牧也。</li><li><strong>六二</strong>：鸣谦，贞吉。象曰：鸣谦贞吉，中心得也。</li><li><strong>九三</strong>：劳谦君子，有终吉。象曰：劳谦君子，万民服也。</li><li><strong>六四</strong>：无不利，(扌为)谦。象曰：无不利，(扌为)谦；不违则也。</li><li><strong>六五</strong>：不富，以其邻，利用侵伐，无不利。象曰：利用侵伐，征不服也。</li><li><strong>上六</strong>：鸣谦，利用行师，征邑国。象曰：鸣谦，志未得也。可用行师，征邑国也。</li></ul><h2 id="第十六卦豫" tabindex="-1">第十六卦豫 <a class="header-anchor" href="#第十六卦豫" aria-label="Permalink to &quot;第十六卦豫&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">雷地豫 震上坤下</p><ul><li><strong>豫</strong>：利建侯行师。</li><li><strong>彖曰</strong>：豫，刚应而志行，顺以动，豫。豫，顺以动，故天地如之，而况建侯行师乎？天地以顺动，故日月不过，而四时不忒；圣人以顺动，则刑罚清而民服。豫之时义大矣哉！</li><li><strong>象曰</strong>：雷出地奋，豫。先王以作乐崇德，殷荐之上帝，以配祖考。</li></ul></div><ul><li><strong>初六</strong>：鸣豫，凶。象 曰：初六鸣豫，志穷凶也。</li><li><strong>六二</strong>：介于石，不终日，贞吉。象曰：不终日，贞吉；以中正也。</li><li><strong>六三</strong>：盱豫，悔。迟有悔。象曰：盱豫有悔，位不当也。</li><li><strong>九四</strong>：由豫，大有得。勿疑。朋盍簪。象曰：由豫，大有得；志大行也。</li><li><strong>六五</strong>：贞疾，恒不死。象曰：六五贞疾，乘刚也。恒不死，中未亡也。</li><li><strong>上六</strong>：冥豫，成有渝，无咎。象曰：冥豫在上，何可长也。</li></ul><h2 id="第十七卦-随" tabindex="-1">第十七卦 随 <a class="header-anchor" href="#第十七卦-随" aria-label="Permalink to &quot;第十七卦 随&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">泽雷随 兑上震下</p><ul><li><strong>随</strong>：元亨利贞，无咎。</li><li><strong>彖曰</strong>：随，刚来而下柔，动而说，随。大亨贞，无咎，而天下随时，随之时义大矣哉！</li><li><strong>象曰</strong>：泽中有雷，随；君子以  晦入宴息。</li></ul></div><ul><li><strong>初九</strong>：官有渝，贞吉。出门交有功。象曰： 官有渝，从正吉也。出门交有功，不失也。</li><li><strong>六二</strong>：系小子，失丈夫。象曰：系小子，弗兼与也。</li><li><strong>六三</strong>：系丈夫，失小子。随有求得，利居贞。象曰：系丈夫，志舍下也。</li><li><strong>九四</strong>：随有获，贞凶。有孚在道，以明，何咎。象曰：随有获，其义凶也。有孚在道，明功也。</li><li><strong>九五</strong>：孚于嘉，吉。象曰：孚于嘉，吉；位正中也。</li><li><strong>上六</strong>：拘系之，乃从维之。王用亨于西山。象曰：拘系之，上穷也。</li></ul><h2 id="第十八卦-蛊" tabindex="-1">第十八卦 蛊 <a class="header-anchor" href="#第十八卦-蛊" aria-label="Permalink to &quot;第十八卦 蛊&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">山风蛊 艮上巽下</p><ul><li><strong>蛊</strong>：元亨，利涉大川。先甲三日，后甲三日。</li><li><strong>彖曰</strong>：蛊，刚上而柔下，巽而止，蛊。蛊，元亨，而天下治也。利涉大川，往有事也。先甲三日，后甲三日，终则有始，天行也。</li><li><strong>象曰</strong>：山下有风，蛊；君子以振民育德。</li></ul></div><ul><li><strong>初六</strong>：干父之蛊，有子，考无咎，厉终吉。象 曰：干父之蛊，意承考也。</li><li><strong>九二</strong>：干母之蛊，不可贞。象曰：干母之蛊，得中道也。</li><li><strong>九三</strong>：干父小有晦，无大咎。象曰：干父之蛊，终无咎也。</li><li><strong>六四</strong>：裕父之蛊，往见吝。象曰：裕父之蛊，往未得也。</li><li><strong>六五</strong>：干父之蛊，用誉。象曰：干父之蛊；承以德也。</li><li><strong>上九</strong>：不事王侯，高尚其事。象曰：不事王侯，志可则也。</li></ul><h2 id="第十九卦-临" tabindex="-1">第十九卦 临 <a class="header-anchor" href="#第十九卦-临" aria-label="Permalink to &quot;第十九卦 临&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">地泽临 坤上兑下</p><ul><li><strong>临</strong>：元，亨，利，贞。至于八月有凶。</li><li><strong>彖曰</strong>：临，刚浸而长。说而顺，刚中而应，大亨以正，天之道也。至于八月有凶，消不久也。</li><li><strong>象曰</strong>：泽上有地，临；君子以教思无穷，容保民无疆。</li></ul></div><ul><li><strong>初九</strong>：咸临，贞吉。象曰：咸临 贞吉，志行正也。</li><li><strong>九二</strong>：咸临，吉无不利。象曰：咸临，吉无不利；未顺命也。</li><li><strong>六三</strong>：甘临，无攸利。既忧之，无咎。象曰：甘临，位不当也。既忧之，咎不长也。</li><li><strong>六四</strong>：至临，无咎。象曰：至临无咎，位当也。</li><li><strong>六五</strong>：知临，大君之宜，吉。象曰：大君之宜，行中之谓也。</li><li><strong>上六</strong>：敦临，吉无咎。象曰：敦临之吉，志在内也。</li></ul><h2 id="第二十卦-观" tabindex="-1">第二十卦 观 <a class="header-anchor" href="#第二十卦-观" aria-label="Permalink to &quot;第二十卦 观&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">风地观 巽上坤下</p><ul><li><strong>观</strong>：盥而不荐，有孚顒若。</li><li><strong>彖曰</strong>：大观在上，顺而巽，中正以观天下。观，盥而不荐，有孚颙若，下观而化也。观天之神道，而四时不忒，圣人以神道设教，而天下服矣。</li><li><strong>象曰</strong>：风行地上，观；先王以省方，观民设教。</li></ul></div><ul><li><strong>初六</strong>：童观，小人无咎，君子吝。象 曰：初六童观，小人道也。</li><li><strong>六二</strong>：窥观，利女贞。象曰：窥观女贞，亦可丑也。</li><li><strong>六三</strong>：观我生，进退。象曰：观我生，进退；未失道也。</li><li><strong>六四</strong>：观国之光，利用宾于王。象曰：观国之光，尚宾也。</li><li><strong>九五</strong>：观我生，君子无咎。象曰：观我生，观民也。</li><li><strong>上九</strong>：观其生，君子无咎。象曰：观其生，志未平也。</li></ul><h2 id="第二十一卦-噬嗑" tabindex="-1">第二十一卦 噬嗑 <a class="header-anchor" href="#第二十一卦-噬嗑" aria-label="Permalink to &quot;第二十一卦 噬嗑&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">火雷噬嗑 离上震下</p><ul><li><strong>噬嗑</strong>：亨。利用狱。</li><li><strong>彖曰</strong>：颐中有物，曰噬嗑，噬嗑而亨。刚柔分，动而明，雷电合而章。柔得中而上行，虽不当位，利用狱也。</li><li><strong>象曰</strong>：雷电噬嗑；先王以明罚敕法。</li></ul></div><ul><li><strong>初九</strong>：履校灭趾，无咎。象曰：履校灭趾，不行 也。</li><li><strong>六二</strong>：噬肤灭鼻，无咎。象曰：噬肤灭鼻，乘刚也。</li><li><strong>六三</strong>：噬腊肉，遇毒；小吝，无咎。象曰：遇毒，位不当也。</li><li><strong>九四</strong>：噬乾胏，得金矢，利艰贞，吉。象曰：利艰贞吉，未光也。六五：噬乾肉，得黄金，贞厉，无咎。象曰：贞厉无咎，得当也。</li><li><strong>上九</strong>：何校灭耳，凶。象曰：何校灭耳，聪不明也。</li></ul><h2 id="第二十二卦-贲" tabindex="-1">第二十二卦 贲 <a class="header-anchor" href="#第二十二卦-贲" aria-label="Permalink to &quot;第二十二卦 贲&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">山火贲 艮上离下</p><ul><li><strong>贲</strong>：亨。小利有所往。</li><li><strong>彖曰</strong>：贲，亨；柔来而文刚，故亨。分刚上而文柔，故小利有攸往。天文也；文明以止，人文也。观乎天文，以察时变；观乎人文，以化成天下。</li><li><strong>象曰</strong>：山下有火，贲；君子以明庶政，无敢折狱。</li></ul></div><ul><li><strong>初九</strong>：贲其趾，舍车而徒。象曰：舍 车而徒，义弗乘也。</li><li><strong>六二</strong>：贲其须。象曰：贲其须，与上兴也。</li><li><strong>九三</strong>：贲如濡如，永贞吉。象曰：永贞之吉，终莫之陵也。</li><li><strong>六四</strong>：贲如皤如，白马翰如，匪寇婚媾。象曰：六四，当位疑也。匪寇婚媾，终无尤也。</li><li><strong>六五</strong>：贲于丘园，束帛戋戋，吝，终吉。象曰：六五之吉，有喜也。</li><li><strong>上九</strong>：白贲，无咎。象曰：白贲无咎，上得志也。</li></ul><h2 id="第二十三卦-剥" tabindex="-1">第二十三卦 剥 <a class="header-anchor" href="#第二十三卦-剥" aria-label="Permalink to &quot;第二十三卦 剥&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">山地剥 艮上坤下</p><ul><li><strong>剥</strong>：不利有攸往。</li><li><strong>彖曰</strong>：剥，剥也，柔变刚也。不利有攸往，小人长也。顺而止之，观象也。君子尚消息盈虚，天行也。</li><li><strong>象曰</strong>：山附地上，剥；上以厚下，安宅。</li></ul></div><ul><li><strong>初六</strong>：剥(爿木)以足，蔑贞凶。象曰：剥(爿木)以足，以灭下也。</li><li><strong>六二</strong>：剥(爿木)以辨，蔑贞凶。象曰：剥(爿木)以辨，未有与也。</li><li><strong>六三</strong>：剥之，无咎。象曰：剥之无咎，失上下也。</li><li><strong>六四</strong>：剥(爿木)以肤，凶。象曰：剥(爿木)以肤，切近灾也。</li><li><strong>六五</strong>：贯鱼，以宫人宠，无不利。象曰：以宫人宠，终无尤也。</li><li><strong>上九</strong>：硕果不食，君子得舆，小人剥庐。象曰：君子得舆，民所载也。小人剥庐，终不可用也。</li></ul><h2 id="第二十四卦-复" tabindex="-1">第二十四卦 复 <a class="header-anchor" href="#第二十四卦-复" aria-label="Permalink to &quot;第二十四卦 复&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">地雷复 坤上震下</p><ul><li><strong>复</strong>：亨。出入无疾，朋来无咎。反复其道，七日来复，利有攸往。</li><li><strong>彖曰</strong>：复亨；刚反，动而以顺行，是以出入无疾，朋来无咎。反复其道，七日来复，天行也。利有攸往，刚长也。复其见天地之心乎？</li><li><strong>象曰</strong>：雷在地中，复；先王以至日闭关，商旅不行，后不省方。</li></ul></div><ul><li><strong>初九</strong>：不复远，无只 悔，元吉。象曰：不远之复，以修身也。</li><li><strong>六二</strong>：休复，吉。象曰：休复之吉，以下仁也。</li><li><strong>六三</strong>：频复，厉无咎。象曰：频复之厉，义无咎也。</li><li><strong>六四</strong>：中行独复。象曰：中行独复，以从道也。</li><li><strong>六五</strong>：敦复，无悔。象曰：敦复无悔，中以自考也。</li><li><strong>上六</strong>：迷复，凶，有灾眚。用行师，终有大败，以其国君，凶；至于十年，不克征。象曰：迷复之凶，反君道也。</li></ul><h2 id="第二十五卦-无妄" tabindex="-1">第二十五卦 无妄 <a class="header-anchor" href="#第二十五卦-无妄" aria-label="Permalink to &quot;第二十五卦 无妄&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">天雷无妄 乾上震下</p><ul><li><strong>无妄</strong>：元，亨，利，贞。其匪正有眚，不利有攸往。</li><li><strong>彖曰</strong>：无妄，刚自外来，而为主於内。动而健，刚中而应，大亨以正，天之命也。其匪正有眚，不利有攸往。无妄之往，何之矣？天命不佑，行矣哉？</li><li><strong>象曰</strong>：天下雷行，物与无妄；先王以茂对时，育万物。</li></ul></div><ul><li><strong>初九</strong>：无妄，往吉。象曰：无妄 之往，得志也。</li><li><strong>六二</strong>：不耕获，不菑畲，则利有攸往。象曰：不耕获，未富也。</li><li><strong>六三</strong>：无妄之灾，或系之牛，行人之得，邑人之灾。象曰：行人得牛，邑人灾也。</li><li><strong>九四</strong>：可贞，无咎。象曰：可贞无咎，固有之也。 九五：无妄之疾，勿药有喜。象曰：无妄之药，不可试也。</li><li><strong>上九</strong>：无妄，行有眚，无攸利。象曰：无妄之行，穷之灾也。</li></ul><h2 id="第二十六卦-大畜" tabindex="-1">第二十六卦 大畜 <a class="header-anchor" href="#第二十六卦-大畜" aria-label="Permalink to &quot;第二十六卦 大畜&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">山天大畜 艮上乾下</p><ul><li><strong>大畜</strong>：利贞，不家食吉，利涉大川。</li><li><strong>彖曰</strong>：大畜，刚健笃实辉光，日新其德，刚上而尚贤。能止健，大正也。不家食吉，养贤也。利涉大川，应乎天也。</li><li><strong>象曰</strong>：天在山中，大畜；君子以多识前言往行，以畜其德。</li></ul></div><ul><li><strong>初九</strong>：有厉利已。象曰：有 厉利已，不犯灾也。</li><li><strong>九二</strong>：舆说辐。象曰：舆说辐，中无尤也。</li><li><strong>九三</strong>：良马逐，利艰贞。曰闲舆卫，利有攸往。象曰：利有攸往，上合志也。</li><li><strong>六四</strong>：童豕之牿，元吉。象曰：六四元吉，有喜也。</li><li><strong>六五</strong>：豶豕之牙，吉。象曰：六五之吉，有庆也。</li><li><strong>上九</strong>：何天之衢，亨。象曰：何天之衢，道大行也。</li></ul><h2 id="第二十七卦-颐" tabindex="-1">第二十七卦 颐 <a class="header-anchor" href="#第二十七卦-颐" aria-label="Permalink to &quot;第二十七卦 颐&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">山雷颐 艮上震下</p><ul><li><strong>颐</strong>：贞吉。观颐，自求口实。</li><li><strong>彖曰</strong>：颐贞吉，养正则吉也。观颐，观其所养也；自求口实，观其自养也。天地养万物，圣人养贤，以及万民；颐之时义大矣哉！</li><li><strong>象曰</strong>：山下有雷，颐；君子以慎言语，节饮食。</li></ul></div><ul><li><strong>初九</strong>：舍尔灵龟，观我朵颐，凶。象 曰：观我朵颐，亦不足贵也。</li><li><strong>六二</strong>：颠颐，拂经，于丘颐，征凶。象曰：六二征凶，行失类也。</li><li><strong>六三</strong>：拂颐，贞凶，十年勿用，无攸利。象曰：十年勿用，道大悖也。</li><li><strong>六四</strong>：颠颐吉，虎视眈眈，其欲逐逐，无咎。象曰：颠颐之吉，上施光也。</li><li><strong>六五</strong>：拂经，居贞吉，不可涉大川。象曰：居贞之吉，顺以从上也。</li><li><strong>上九</strong>：由颐，厉吉，利涉大川。象曰：由颐厉吉，大有庆也。</li></ul><h2 id="第二十八卦-大过" tabindex="-1">第二十八卦 大过 <a class="header-anchor" href="#第二十八卦-大过" aria-label="Permalink to &quot;第二十八卦 大过&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">泽风大过 兑上巽下</p><ul><li><strong>大过</strong>：栋桡，利有攸往，亨。</li><li><strong>彖曰</strong>：大过，大者过也。栋桡，本末弱也。刚过而中，巽而说行，利有攸往，乃亨。大过之时义大矣哉！</li><li><strong>象曰</strong>：泽灭木，大过；君子以独立不惧，（辶豚）世无闷。</li></ul></div><ul><li><strong>初六</strong>：藉用白茅，无咎。象曰：藉用白茅，柔在下也。</li><li><strong>九二</strong>：枯杨生稊，老夫得其女妻，无不利。象曰：老夫女妻，过以相与也。</li><li><strong>九三</strong>：栋桡，凶。象曰：栋桡之凶，不可以有辅也。</li><li><strong>九四</strong>：栋隆，吉；有它吝。象曰：栋隆之吉，不桡乎下也。</li><li><strong>九五</strong>：枯杨生华，老妇得士夫，无咎无誉。象曰：枯杨生华，何可久也。老妇士夫，亦可丑也。</li><li><strong>上六</strong>：过涉灭顶，凶，无咎。象曰：过涉之凶，不可咎也。</li></ul><h2 id="第二十九卦-坎" tabindex="-1">第二十九卦 坎 <a class="header-anchor" href="#第二十九卦-坎" aria-label="Permalink to &quot;第二十九卦 坎&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">坎为水 坎上坎下</p><ul><li><strong>坎</strong>：习坎，有孚，维心亨，行有尚。</li><li><strong>彖曰</strong>：习坎，重险也。水流而不盈，行险而不失其信。维心亨，乃以刚中也。行有尚，往有功也。天险不可升也，地险山川丘陵也，王公设险以守其国，坎之时用大矣哉！</li><li><strong>象曰</strong>：水洊至，习坎；君子以常德行，习教事。</li></ul></div><ul><li><strong>初六</strong>：习坎，入于坎窞，凶。象曰：习坎入坎，失道凶也。</li><li><strong>九二</strong>：坎有险，求小得。象曰：求小得，未出中也。</li><li><strong>六三</strong>：来之坎坎，险且枕，入于坎窞，勿用。象曰：来之坎坎，终无功也。</li><li><strong>六四</strong>：樽酒簋贰，用缶，纳约自牖，终无咎。象曰：樽酒簋贰，刚柔际也。</li><li><strong>九五</strong>：坎不盈，只既平，无咎。象曰：坎不盈，中未大也。</li><li><strong>上六</strong>：用徽绎，置于丛棘，三岁不得，凶。象曰：上六失道，凶三岁也。</li></ul><h2 id="第三十卦-离" tabindex="-1">第三十卦 离 <a class="header-anchor" href="#第三十卦-离" aria-label="Permalink to &quot;第三十卦 离&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">离为火 离上离下</p><ul><li><strong>离</strong>：利贞，亨。畜牝牛，吉。</li><li><strong>彖曰</strong>：离，丽也；日月丽乎天，百谷草木丽乎土，重明以丽乎正，乃化成天下。柔丽乎中正，故亨；是以畜牝牛吉也。</li><li><strong>象曰</strong>：明两作离，大人以继明照于四方。</li></ul></div><ul><li><strong>初九</strong>：履错然，敬之无咎。象曰：履错之敬， 以辟咎也。</li><li><strong>六二</strong>：黄离，元吉。象曰：黄离元吉，得中道也。</li><li><strong>九三</strong>：日昃之离，不鼓缶而歌，则大耋之嗟，凶。象曰：日昃之离，何可久也。</li><li><strong>九四</strong>：突如其来如，焚如，死如，弃如。象曰：突如其来如，无所容也。</li><li><strong>六五</strong>：出涕沱若，戚嗟若，吉。象曰：六五之吉，离王公也。</li><li><strong>上九</strong>：王用出征，有嘉折首，获其匪丑，无咎。象曰：王用出征，以正邦也。</li></ul><h2 id="第三十一卦-咸" tabindex="-1">第三十一卦 咸 <a class="header-anchor" href="#第三十一卦-咸" aria-label="Permalink to &quot;第三十一卦 咸&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">泽山咸 兑上艮下</p><ul><li><strong>咸</strong>：亨，利贞，取女吉。</li><li><strong>彖曰</strong>：咸，感也。柔上而刚下，二气感应以相与，止而说，男下女，是以亨利贞，取女吉也。天地感而万物化生，圣人感人心而天下和平；观其所感，而天地万物之情可见矣！</li><li><strong>象曰</strong>：山上有泽，咸；君子以虚受人。</li></ul></div><ul><li><strong>初六</strong>：咸其拇。象曰：咸其拇，志在外也。</li><li><strong>六二</strong>：咸其腓，凶，居吉。象曰：虽凶，居吉，顺不害也。</li><li><strong>九三</strong>：咸其股，执其随，往吝。象曰：咸其股，亦不处也。志在随人，所执下也。</li><li><strong>九四</strong>：贞吉悔亡，憧憧往来，朋从尔思。象曰：贞吉悔亡，未感害也。憧憧往来，未光大也。</li><li><strong>九五</strong>：咸其脢，无悔。象曰：咸其脢，志末也。</li><li><strong>上六</strong>：咸其辅，颊，舌。象曰：咸其辅，颊，舌，滕口说也。</li></ul><h2 id="第三十二卦-恒" tabindex="-1">第三十二卦 恒 <a class="header-anchor" href="#第三十二卦-恒" aria-label="Permalink to &quot;第三十二卦 恒&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">雷风恒 震上巽下</p><ul><li><strong>恒</strong>：亨，无咎，利贞，利有攸往。</li><li><strong>彖曰</strong>：恒，久也。刚上而柔下，雷风相与，巽而动，刚柔皆应，恒。恒亨无咎，利贞；久於其道也，天地之道，恒久而不已也。利有攸往，终则有始也。日月得天，而能久照，四 时变化，而能久成，圣人久於其道，而天下化成；观其所恒，而天地万物之情可见矣！</li><li><strong>象曰</strong>：雷风，恒；君子以立不易方。</li></ul></div><ul><li><strong>初六</strong>：浚恒，贞凶，无攸利。象曰：浚恒之凶，始 求深也。</li><li><strong>九二</strong>：悔亡。象曰：九二悔亡，能久中也。</li><li><strong>九三</strong>：不恒其德，或承之羞，贞吝。象曰：不恒其德，无所容也。</li><li><strong>九四</strong>：田无禽。象曰：久非其位，安得禽也。</li><li><strong>六五</strong>：恒其德，贞，妇人吉，夫子凶。象曰：妇人贞吉，从一而终也。夫子制义，从妇凶也。</li><li><strong>上六</strong>：振恒，凶。象曰：振恒在上，大无功也。</li></ul><h2 id="第三十三卦-遁" tabindex="-1">第三十三卦 遁 <a class="header-anchor" href="#第三十三卦-遁" aria-label="Permalink to &quot;第三十三卦 遁&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">天山遁 乾上艮下</p><ul><li><strong>遁</strong>：亨，小利贞。</li><li><strong>彖曰</strong>：遁亨，遁而亨也。刚当位而应，与时行也。小利贞，浸而长也。遁之时义大矣哉！</li><li><strong>象曰</strong>：天下有山，遁；君子以远小人，不恶而严。</li></ul></div><ul><li><strong>初六</strong>：遁尾，厉，勿用有攸往。象曰：遁尾之厉，不往何灾也。</li><li><strong>六二</strong>：执之用黄牛之革，莫之胜说。象曰：执用黄牛，固志也。</li><li><strong>九三</strong>：系遁，有疾厉，畜臣妾吉。象曰：系遁之厉，有疾惫也。畜臣妾吉，不可大事也。</li><li><strong>九四</strong>：好遁，君子吉，小人否。象曰：君子好遁，小人否也。</li><li><strong>九五</strong>：嘉遁，贞吉。象曰：嘉遁贞吉，以正志也。</li><li><strong>上九</strong>：肥遁，无不利。象曰：肥遁，无不利；无所疑也。</li></ul><h2 id="第三十四卦-大壮" tabindex="-1">第三十四卦 大壮 <a class="header-anchor" href="#第三十四卦-大壮" aria-label="Permalink to &quot;第三十四卦 大壮&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">雷天大壮 震上乾下</p><ul><li><strong>大壮</strong>：利贞。</li><li><strong>彖曰</strong>：大壮，大者壮也。刚以动，故壮。大壮利贞；大者正也。正大而天地之情可见矣！</li><li><strong>象曰</strong>：雷在天上，大壮；君子以非礼勿履。</li></ul></div><ul><li><strong>初九</strong>：壮于趾，征凶，有孚。象曰：壮于 趾，其孚穷也。</li><li><strong>九二</strong>：贞吉。象曰：九二贞吉，以中也。</li><li><strong>九三</strong>：小人用壮，君子用罔，贞厉。羝羊触藩，羸其角。象曰：小人用壮，君子罔也。</li><li><strong>九四</strong>：贞吉悔亡，藩决不羸，壮于大舆之輹。象曰：藩决不羸，尚往也。</li><li><strong>六五</strong>：丧羊于易，无悔。象曰：丧羊于易，位不当也。</li><li><strong>上六</strong>：羝羊触藩，不能退，不能遂，无攸利，艰则吉。象曰：不能退，不能遂，不祥也。艰则吉，咎不长也。</li></ul><h2 id="第三十五卦-晋" tabindex="-1">第三十五卦 晋 <a class="header-anchor" href="#第三十五卦-晋" aria-label="Permalink to &quot;第三十五卦 晋&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">火地晋 离上坤下</p><ul><li><strong>晋</strong>：康侯用锡马蕃庶，昼日三接。</li><li><strong>彖曰</strong>：晋，进也。明出地上，顺而丽乎大明，柔进而上行。是以康侯用锡马蕃庶，昼日三接也。</li><li><strong>象曰</strong>：明出地上，晋；君子以自昭明德。</li></ul></div><ul><li><strong>初六</strong>：晋如，摧如，贞吉。罔孚，裕无咎。象 曰：晋如，摧如；独行正也。裕无咎；未受命也。</li><li><strong>六二</strong>：晋如，愁如，贞吉。受兹介福，于其王母。象曰：受之介福，以中正也。</li><li><strong>六三</strong>：众允，悔亡。象曰：众允之，志上行也。</li><li><strong>九四</strong>：晋如硕鼠，贞厉。象曰：硕鼠贞厉，位不当也。</li><li><strong>六五</strong>：悔亡，失得勿恤，往吉无不利。象曰：失得勿恤，往有庆也。</li><li><strong>上九</strong>：晋其角，维用伐邑，厉吉无咎，贞吝。象曰：维用伐邑，道未光也。</li></ul><h2 id="第三十六卦-明夷" tabindex="-1">第三十六卦 明夷 <a class="header-anchor" href="#第三十六卦-明夷" aria-label="Permalink to &quot;第三十六卦 明夷&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">地火明夷 坤上离下</p><ul><li><strong>明夷</strong>：利艰贞。</li><li><strong>彖曰</strong>：明入地中，明夷。内文明而外柔顺，以蒙大难，文王以之。利艰贞，晦其明也，内难而能正其志，箕子以之。</li><li><strong>象曰</strong>：明入地中，明夷；君子以莅众，用晦而明。</li></ul></div><ul><li><strong>初九</strong>：明夷于飞，垂其翼。君子于 行，三日不食，有攸往，主人有言。象曰：君子于行，义不食也。</li><li><strong>六二</strong>：明夷，夷于左股，用拯马壮，吉。象曰：六二之吉，顺以则也。</li><li><strong>九三</strong>：明夷于南狩，得其大首，不可疾贞。象曰：南狩之志，乃大得也。</li><li><strong>六四</strong>：入于左腹，获明夷之心，出于门庭。象曰：入于左腹，获心意也。</li><li><strong>六五</strong>：箕子之明夷，利贞。象曰：箕子之贞，明不可息也。</li><li><strong>上六</strong>：不明晦，初登于天，后入于地。象曰：初登于天，照四国也。后入于地，失则也。</li></ul><h2 id="第三十七卦-家人" tabindex="-1">第三十七卦 家人 <a class="header-anchor" href="#第三十七卦-家人" aria-label="Permalink to &quot;第三十七卦 家人&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">风火家人 巽上离下</p><ul><li><strong>家人</strong>：利女贞。</li><li><strong>彖曰</strong>：家人，女正位乎内，男正位乎外，男女正，天地之大义也。家人有严君焉，父母之谓也。父父，子子，兄兄，弟弟，夫夫，妇妇，而家道正；正家而天下定矣。</li><li><strong>象曰</strong>：风自火出，家人；君子以言有物，而行有恒。</li></ul></div><ul><li><strong>初九</strong>：闲有家，悔亡。象曰：闲有家，志未变也。</li><li><strong>六二</strong>：无攸遂，在中馈，贞吉。象曰：六二之吉，顺以巽也。</li><li><strong>九三</strong>：家人嗃嗃，悔厉吉；妇子嘻嘻，终吝。象曰：家人嗃嗃，未失也；妇子嘻嘻，失家节也。</li><li><strong>六四</strong>：富家，大吉。象曰：富家大吉，顺在位也。</li><li><strong>九五</strong>：王假有家，勿恤吉。象曰：王假有家，交相爱也。</li><li><strong>上九</strong>：有孚威如，终吉。象曰：威如之吉，反身之谓也。</li></ul><h2 id="第三十八卦-睽" tabindex="-1">第三十八卦 睽 <a class="header-anchor" href="#第三十八卦-睽" aria-label="Permalink to &quot;第三十八卦 睽&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">火泽睽 离上兑下</p><ul><li><strong>睽</strong>：小事吉。</li><li><strong>彖曰</strong>：睽，火动而上，泽动而下；二女同居，其志不同行；说而丽乎明，柔进而上行，得中而应乎刚；是以小事吉。天地睽，而其事同也；男女睽，而其志通也；万物睽，而其事 类也；睽之时用大矣哉！</li><li><strong>象曰</strong>：上火下泽，睽；君子以同而异。</li></ul></div><ul><li><strong>初九</strong>：悔亡，丧马勿逐，自复；见恶人无咎。象 曰：见恶人，以辟咎也。</li><li><strong>九二</strong>：遇主于巷，无咎。象曰：遇主于巷，未失道也。</li><li><strong>六三</strong>：见舆曳，其牛掣，其人天且劓，无初有终。象曰：见舆曳，位不当也。无初有终，遇刚也。</li><li><strong>九四</strong>：睽孤，遇元夫，交孚，厉无咎。象曰：交孚无咎，志行也。</li><li><strong>六五</strong>：悔亡，厥宗噬肤，往何咎。象曰：厥宗噬肤，往有庆也。</li><li><strong>上九</strong>：睽孤，见豕负涂，载鬼一车，先张之弧，后说之弧，匪寇婚媾，往遇雨则吉。象曰：遇雨之吉，群疑亡也。</li></ul><h2 id="第三十九卦-蹇" tabindex="-1">第三十九卦 蹇 <a class="header-anchor" href="#第三十九卦-蹇" aria-label="Permalink to &quot;第三十九卦 蹇&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">水山蹇 坎上艮下</p><ul><li><strong>蹇</strong>：利西南，不利东北；利见大人，贞吉。</li><li><strong>彖曰</strong>：蹇，难也，险在前也。见险而能止，知矣哉！蹇利西南，往得中也；不利东北，其道穷也。利见大人，往有功也。当位贞吉，以正邦也。蹇之时用大矣哉！</li><li><strong>象曰</strong>：山上有水，蹇；君子以反身修德。</li></ul></div><ul><li><strong>初六</strong>：往蹇，来誉。象曰：往蹇来誉，宜待也。</li><li><strong>六二</strong>：王臣蹇蹇，匪躬之故。象曰：王臣蹇蹇，终无尤也。</li><li><strong>九三</strong>：往蹇来反。象曰：往蹇来反，内喜之也。</li><li><strong>六四</strong>：往蹇来连。象曰：往蹇来连，当位实也。</li><li><strong>九五</strong>：大蹇朋来。象曰：大蹇朋来，以中节也。</li><li><strong>上六</strong>：往蹇来硕，吉；利见大人。象曰：往蹇来硕，志在内也。利见大人，以从贵也。</li></ul><h2 id="第四十卦-解" tabindex="-1">第四十卦 解 <a class="header-anchor" href="#第四十卦-解" aria-label="Permalink to &quot;第四十卦 解&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">雷水解 震上坎下</p><ul><li><strong>解</strong>：利西南，无所往，其来复吉。有攸往，夙吉。</li><li><strong>彖曰</strong>：解，险以动，动而免乎险，解。解利西南，往得众也。其来复吉，乃得中也。有攸往夙吉，往有功也。天地解，而雷雨作，雷雨作，而百果草木皆甲坼，解之时义大矣哉！</li><li><strong>象曰</strong>：雷雨作，解；君子以赦过宥罪。</li></ul></div><ul><li><strong>初六</strong>：无咎。象曰：刚柔之际，义无咎也。</li><li><strong>九二</strong>：田获三狐，得黄矢，贞吉。象曰：九二贞吉，得中道也。</li><li><strong>六三</strong>：负且乘，致寇至，贞吝。象曰：负且乘，亦可丑也，自我致戎，又谁咎也。</li><li><strong>九四</strong>：解而拇，朋至斯孚。象曰：解而拇，未当位也。</li><li><strong>六五</strong>：君子维有解，吉；有孚于小人。象曰：君子有解，小人退也。</li><li><strong>上六</strong>：公用射隼，于高墉之上，获之，无不利。象曰：公用射隼，以解悖也。</li></ul><h2 id="第四十一卦-损" tabindex="-1">第四十一卦 损 <a class="header-anchor" href="#第四十一卦-损" aria-label="Permalink to &quot;第四十一卦 损&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">山泽损 艮上兑下</p><ul><li><strong>损</strong>：有孚，元吉，无咎，可贞，利有攸往？曷之用，二簋可用享。</li><li><strong>彖曰</strong>：损，损下益上，其道上行。损而有孚，元吉，无咎，可贞，利有攸往。曷之用？二簋可用享；二簋应有时。损刚益柔有时，损益盈虚，与时偕行。</li><li><strong>象曰</strong>：山下有泽，损；君子以惩忿窒欲。</li></ul></div><ul><li><strong>初九</strong>：已事遄往，无咎，酌损之。象曰：已事 遄往，尚合志也。</li><li><strong>九二</strong>：利贞，征凶，弗损益之。象曰：九二利贞，中以为志也。</li><li><strong>六三</strong>：三人行，则损一人；一人行，则得其友。象曰：一人行，三则疑也。</li><li><strong>六四</strong>：损其疾，使遄有喜，无咎。象曰：损其疾，亦可喜也。</li><li><strong>六五</strong>：或益之，十朋之龟弗克违，元吉。象曰：六五元吉，自上佑也。</li><li><strong>上九</strong>：弗损益之，无咎，贞吉，利有攸往，得臣无家。象曰：弗损益之，大得志也。</li></ul><h2 id="第四十二卦-益" tabindex="-1">第四十二卦 益 <a class="header-anchor" href="#第四十二卦-益" aria-label="Permalink to &quot;第四十二卦 益&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">风雷益 巽上震下</p><ul><li><strong>益</strong>：利有攸往，利涉大川。</li><li><strong>彖曰</strong>：益，损上益下，民说无疆，自上下下，其道大光。利有攸往，中正有庆。利涉大川，木道乃行。益动而巽，日进无疆。天施地生，其益无方。凡益之道，与时偕行。</li><li><strong>象曰</strong>：风雷，益；君子以见善则迁，有过则改。</li></ul></div><ul><li><strong>初九</strong>：利用为大作，元吉，无咎。象 曰：元吉无咎，下不厚事也。</li><li><strong>六二</strong>：或益之，十朋之龟弗克违，永贞吉。王用享于帝，吉。象曰：或益之，自外来也。</li><li><strong>六三</strong>：益之用凶事，无咎。有孚中行，告公用圭。象曰：益用凶事，固有之也。</li><li><strong>六四</strong>：中行，告公从。利用为依迁国。象曰：告公从，以益志也。</li><li><strong>九五</strong>：有孚惠心，勿问元吉。有孚惠我德。象曰：有孚惠心，勿问之矣。惠我德，大得志也。</li><li><strong>上九</strong>：莫益之，或击之，立心勿恒，凶。象曰：莫益之，偏辞也。或击之，自外来也。</li></ul><h2 id="第四十三卦-夬" tabindex="-1">第四十三卦 夬 <a class="header-anchor" href="#第四十三卦-夬" aria-label="Permalink to &quot;第四十三卦 夬&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">泽天夬 兑上乾下</p><ul><li><strong>夬</strong>：扬于王庭，孚号，有厉，告自邑，不利即戎，利有攸往。</li><li><strong>彖曰</strong>：夬，决也，刚决柔也。健而说，决而和，扬于王庭，柔乘五刚也。孚号有厉，其危乃光也。告自邑，不利即戎，所尚乃穷也。利有攸往，刚长乃终也。</li><li><strong>象曰</strong>：泽上于天，夬；君子以施禄及下，居德则忌。</li></ul></div><ul><li><strong>初九</strong>：壮于前趾，往不胜为吝。 象曰：不胜而往，咎也。</li><li><strong>九二</strong>：惕号，莫夜有戎，勿恤。象曰：莫夜有戎，得中道也。</li><li><strong>九三</strong>：壮于，有凶。君子夬夬，独行遇雨，若濡有愠，无咎。象曰：君子夬夬，终无咎也。</li><li><strong>九四</strong>：臀无肤，其行次且。牵羊悔亡，闻言不信。象曰：其行次且，位不当也。闻言不信，聪不明也。</li><li><strong>九五</strong>：苋陆夬夬，中行无咎。象曰：中行无咎，中未光也。</li><li><strong>上六</strong>：无号，终有凶。象曰：无号之凶，终不可长也。</li></ul><h2 id="第四十四卦-姤" tabindex="-1">第四十四卦 姤 <a class="header-anchor" href="#第四十四卦-姤" aria-label="Permalink to &quot;第四十四卦 姤&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">天风姤 乾上巽下</p><ul><li><strong>姤</strong>：女壮，勿用取女。</li><li><strong>彖曰</strong>：姤，遇也，柔遇刚也。勿用取女，不可与长也。天地相遇，品物咸章也。刚遇中正，天下大行也。姤之时义大矣哉！</li><li><strong>象曰</strong>：天下有风，姤；后以施命诰四方。</li></ul></div><ul><li><strong>初六</strong>：系于金柅，贞吉，有攸往，见凶， 羸豕踟躅。象曰：系于金柅，柔道牵也。</li><li><strong>九二</strong>：包有鱼，无咎，不利宾。象曰：包有鱼，义不及宾也。</li><li><strong>九三</strong>：臀无肤，其行次且，厉，无大咎。象曰：其行次且，行未牵也。</li><li><strong>九四</strong>：包无鱼，起凶。象曰：无鱼之凶，远民也。</li><li><strong>九五</strong>：以杞包瓜，含章，有陨自天。象曰：九五含章，中正也。有陨自天，志不舍命也。</li><li><strong>上九</strong>：姤其角，吝，无咎。象曰：姤其角，上穷吝也。</li></ul><h2 id="第四十五卦-萃" tabindex="-1">第四十五卦 萃 <a class="header-anchor" href="#第四十五卦-萃" aria-label="Permalink to &quot;第四十五卦 萃&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">泽地萃 兑上坤下</p><ul><li><strong>萃</strong>：亨。王假有庙，利见大人，亨，利贞。用大牲吉，利有攸往。</li><li><strong>彖曰</strong>：萃，聚也；顺以说，刚中而应，故聚也。王假有庙，致孝享也。利见大人亨，聚以正也。用大牲吉，利有攸往，顺天命也。观其所聚，而天地万物之情可见矣。</li><li><strong>象曰</strong>：泽上於地，萃；君子以除戎器，戒不虞。</li></ul></div><ul><li><strong>初六</strong>：有孚不终，乃乱乃萃，若号一握 为笑，勿恤，往无咎。象曰：乃乱乃萃，其志乱也。</li><li><strong>六二</strong>：引吉，无咎，孚乃利用禴。象曰：引吉无咎，中未变也。</li><li><strong>六三</strong>：萃如，嗟如，无攸利，往无咎，小吝。象曰：往无咎，上巽也。</li><li><strong>九四</strong>：大吉，无咎。象曰：大吉无咎，位不当也。</li><li><strong>九五</strong>：萃有位，无咎。匪孚，元永贞，悔亡。象曰：萃有位，志未光也。</li><li><strong>上六</strong>：赍咨涕洟，无咎。象曰：赍咨涕洟，未安上也。</li></ul><h2 id="第四十六卦-升" tabindex="-1">第四十六卦 升 <a class="header-anchor" href="#第四十六卦-升" aria-label="Permalink to &quot;第四十六卦 升&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">地风升 坤上巽下</p><ul><li><strong>升</strong>：元亨，用见大人，勿恤，南征吉。</li><li><strong>彖曰</strong>：柔以时升，巽而顺，刚中而应，是以大亨。用见大人，勿恤；有庆也。南征吉，志行也。</li><li><strong>象曰</strong>：地中生木，升；君子以顺德，积小以高大。</li></ul></div><ul><li><strong>初六</strong>：允升，大吉。象曰：允升大 吉，上合志也。</li><li><strong>九二</strong>：孚乃利用禴，无咎。象曰：九二之孚，有喜也。</li><li><strong>九三</strong>：升虚邑。象曰：升虚邑，无所疑也。</li><li><strong>六四</strong>：王用亨于岐山，吉无咎。象曰：王用亨于岐山，顺事也。</li><li><strong>六五</strong>：贞吉，升阶。象曰：贞吉升阶，大得志也。</li><li><strong>上六</strong>：冥升，利于不息之贞。象曰：冥升在上，消不富也。</li></ul><h2 id="第四十七卦-困" tabindex="-1">第四十七卦 困 <a class="header-anchor" href="#第四十七卦-困" aria-label="Permalink to &quot;第四十七卦 困&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">泽水困 兑上坎下</p><ul><li><strong>困</strong>：亨，贞，大人吉，无咎，有言不信。</li><li><strong>彖曰</strong>：困，刚掩也。险以说，困而不失其所，亨；其唯君子乎？贞大人吉，以刚中也。有言不信，尚口乃穷也。</li><li><strong>象曰</strong>：泽无水，困；君子以致命遂志。</li></ul></div><ul><li><strong>初六</strong>：臀困于株木，入于幽谷，三岁不见。象 曰：入于幽谷，幽不明也。</li><li><strong>九二</strong>：困于酒食，朱绂方来，利用亨祀，征凶，无咎。象曰：困于酒食，中有庆也。</li><li><strong>六三</strong>：困于石，据于蒺藜，入于其宫，不见其妻，凶。象曰：据于蒺藜，乘刚也。入于其宫，不见其妻，不祥也。</li><li><strong>九四</strong>：来徐徐，困于金车，吝，有终。象曰：来徐徐，志在下也。虽不当位，有与也。</li><li><strong>九五</strong>：劓刖，困于赤绂，乃徐有说，利用祭祀。象曰：劓刖，志未得也。乃徐有说，以中直也。利用祭祀，受福也。</li><li><strong>上六</strong>：困于葛藟，于臲卼，曰动悔。有悔，征吉。象曰：困于葛藟，未当也。动悔，有悔吉，行也。</li></ul><h2 id="第四十八卦-井" tabindex="-1">第四十八卦 井 <a class="header-anchor" href="#第四十八卦-井" aria-label="Permalink to &quot;第四十八卦 井&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">水风井 坎上巽下</p><ul><li><strong>井</strong>：改邑不改井，无丧无得，往来井井。汔至，亦未繘井，羸其瓶，凶。</li><li><strong>彖曰</strong>：巽乎水而上水，井；井养而不穷也。改邑不改井，乃以刚中也。汔至亦未繘井，未有功也。羸其瓶，是以凶也。</li><li><strong>象曰</strong>：木上有水，井；君子以劳民劝相。</li></ul></div><ul><li><strong>初六</strong>：井泥不食，旧井无禽。象曰：井泥不 食，下也。旧井无禽，时舍也。</li><li><strong>九二</strong>：井谷射鲋，瓮敝漏。象曰：井谷射鲋，无与也。</li><li><strong>九三</strong>：井渫不食，为我民恻，可用汲，王明，并受其福。象曰：井渫不食，行恻也。求王明，受福也。</li><li><strong>六四</strong>：井甃，无咎。象曰：井甃无咎，修井也。</li><li><strong>九五</strong>：井冽，寒泉食。象曰：寒泉之食，中正也。</li><li><strong>上六</strong>：井收勿幕，有孚无吉。象曰：元吉在上，大成也。</li></ul><h2 id="第四十九卦-革" tabindex="-1">第四十九卦 革 <a class="header-anchor" href="#第四十九卦-革" aria-label="Permalink to &quot;第四十九卦 革&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">泽火革 兑上离下</p><ul><li><strong>革</strong>：己日乃孚，元亨利贞，悔亡。</li><li><strong>彖曰</strong>：革，水火相息，二女同居，其志不相得，曰革。己日乃孚；革而信也。文明以说，大亨以正，革而当，其悔乃亡。天地革而四时成，汤武革命，顺乎天而应乎人，革之时 义大矣哉！ 象曰：泽中有火，革；君子以治历明时。初九：巩用黄牛之革。</li><li><strong>象曰</strong>：巩用黄牛，不可 以有为也。</li></ul></div><ul><li><strong>六二</strong>：己日乃革之，征吉，无咎。象曰：己日革之，行有嘉也。</li><li><strong>九三</strong>：征凶，贞厉，革言三就，有孚。象曰：革言三就，又何之矣。</li><li><strong>九四</strong>：悔亡，有孚改命，吉。象曰：改命之吉，信志也。</li><li><strong>九五</strong>：大人虎变，未占有孚。象曰：大人虎变，其文炳也。</li><li><strong>上六</strong>：君子豹变，小人革面，征凶，居贞吉。象曰：君子豹变，其文蔚也。小人革面，顺以从君也。</li></ul><h2 id="第五十卦-鼎" tabindex="-1">第五十卦 鼎 <a class="header-anchor" href="#第五十卦-鼎" aria-label="Permalink to &quot;第五十卦 鼎&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">火风鼎 离上巽下</p><ul><li><strong>鼎</strong>：元吉，亨。</li><li><strong>彖曰</strong>：鼎，象也。以木巽火，亨饪也。圣人亨以享上帝，而大亨以养圣贤。巽而耳目聪明，柔进而上行，得中而应乎刚，是以元亨。</li><li><strong>象曰</strong>：木上有火，鼎；君子以正位凝命。</li></ul></div><ul><li><strong>初六</strong>：鼎颠趾，利出否，得妾以其子，无咎。 象曰：鼎颠趾，未悖也。利出否，以从贵也。</li><li><strong>九二</strong>：鼎有实，我仇有疾，不我能即，吉。象曰：鼎有实，慎所之也。我仇有疾，终无尤也。</li><li><strong>九三</strong>：鼎耳革，其行塞，雉膏不食，方雨亏悔，终吉。象曰：鼎耳革，失其义也。</li><li><strong>九四</strong>：鼎折足，覆公 □，其形渥，凶。象曰：覆公束，信如何也。</li><li><strong>六五</strong>：鼎黄耳金铉，利贞。象曰：鼎黄耳，中以为实也。</li><li><strong>上九</strong>：鼎玉铉，大吉，无不利。象曰：玉铉在上，刚柔节也。</li></ul><h2 id="第五十一卦-震" tabindex="-1">第五十一卦 震 <a class="header-anchor" href="#第五十一卦-震" aria-label="Permalink to &quot;第五十一卦 震&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">震为雷 震上震下</p><ul><li><strong>震</strong>：亨。震来虩虩，笑言哑哑。震惊百里，不丧匕鬯。</li><li><strong>彖曰</strong>：震，亨。震来虩虩，恐致福也。笑言哑哑，后有则也。震惊百里，惊远而惧迩也。出可以守宗庙社稷，以为祭主也。</li><li><strong>象曰</strong>：洊雷，震；君子以恐惧修身。</li></ul></div><ul><li><strong>初九</strong>：震来虩虩，后笑言哑哑，吉。象曰： 震来虩虩，恐致福也。笑言哑哑，后有则也。</li><li><strong>六二</strong>：震来厉，亿丧贝，跻于九陵，勿逐，七日得。象曰：震来厉，乘刚也。</li><li><strong>六三</strong>：震苏苏，震行无眚。象曰：震苏苏，位不当也。</li><li><strong>九四</strong>：震遂泥。象曰：震遂泥，未光也。</li><li><strong>六五</strong>：震往来厉，亿无丧，有事。象曰：震往来厉，危行也。其事在中，大无丧也。</li><li><strong>上六</strong>：震索索，视矍矍，征凶。震不于其躬，于其邻，无咎。婚媾有言。象曰：震索索，未得中也。虽凶无咎，畏邻戒也。</li></ul><h2 id="第五十二卦-艮" tabindex="-1">第五十二卦 艮 <a class="header-anchor" href="#第五十二卦-艮" aria-label="Permalink to &quot;第五十二卦 艮&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">艮为山 艮上艮下</p><ul><li><strong>艮</strong>：艮其背，不获其身，行其庭，不见其人，无咎。</li><li><strong>彖曰</strong>：艮，止也。时止则止，时行则行，动静不失其时，其道光明。艮其止，止其所也。上下敌应，不相与也。是以不获其身，行其庭不见其人，无咎也。</li><li><strong>象曰</strong>：兼山，艮；君子以思不出其位。</li></ul></div><ul><li><strong>初六</strong>：艮其趾，无咎，利永贞。象曰：艮其趾， 未失正也。</li><li><strong>六二</strong>：艮其腓，不拯其随，其心不快。象曰：不拯其随，未退听也。</li><li><strong>九三</strong>：艮其限，列其夤，厉薰心。象曰：艮其限，危薰心也。</li><li><strong>六四</strong>：艮其身，无咎。象曰：艮其身，止诸躬也。</li><li><strong>六五</strong>：艮其辅，言有序，悔亡。象曰：艮其辅，以中正也。</li><li><strong>上九</strong>：敦艮，吉。象曰：敦艮之吉，以厚终也。</li></ul><h2 id="第五十三卦-渐" tabindex="-1">第五十三卦 渐 <a class="header-anchor" href="#第五十三卦-渐" aria-label="Permalink to &quot;第五十三卦 渐&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">风山渐 巽上艮下</p><ul><li><strong>渐</strong>：女归吉，利贞。</li><li><strong>彖曰</strong>：渐之进也，女归吉也。进得位，往有功也。进以正，可以正邦也。其位刚，得中也。止而巽，动不穷也。</li><li><strong>象曰</strong>：山上有木，渐；君子以居贤德，善俗。</li></ul></div><ul><li><strong>初六</strong>：鸿渐于干，小子厉，有言，无咎。 象曰：小子之厉，义无咎也。</li><li><strong>六二</strong>：鸿渐于磐，饮食衍衍，吉。象曰：饮食衍衍，不素饱也。</li><li><strong>九三</strong>：鸿渐于陆，夫征不复，妇孕不育，凶；利御寇。象曰：夫征不复，离群丑也。妇孕不育，失其道也。利用御寇，顺相保也。</li><li><strong>六四</strong>：鸿渐于木，或得其桷，无咎。象曰：或得其桷，顺以巽也。</li><li><strong>九五</strong>：鸿渐于陵，妇三岁不孕，终莫之胜，吉。象曰：终莫之胜，吉；得所愿也。</li><li><strong>上九</strong>：鸿渐于逵，其羽可用为仪，吉。象曰：其羽可用为仪，吉；不可乱也。</li></ul><h2 id="第五十四卦-归妹" tabindex="-1">第五十四卦 归妹 <a class="header-anchor" href="#第五十四卦-归妹" aria-label="Permalink to &quot;第五十四卦 归妹&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">雷泽归妹 震上兑下</p><ul><li><strong>归妹</strong>：征凶，无攸利。</li><li><strong>彖曰</strong>：归妹，天地之大义也。天地不交，而万物不兴，归妹人之终始也。说以动，所归妹也。征凶，位不当也。无攸利，柔乘刚也。</li><li><strong>象曰</strong>：泽上有雷，归妹；君子以永终知敝。</li></ul></div><ul><li><strong>初九</strong>：归妹以娣，跛能履，征吉。象曰：归 妹以娣，以恒也。跛能履吉，相承也。</li><li><strong>九二</strong>：眇能视，利幽人之贞。象曰：利幽人之贞，未变常也。</li><li><strong>六三</strong>：归妹以须，反归以娣。象曰：归妹以须，未当也。</li><li><strong>九四</strong>：归妹愆期，迟归有时。象曰：愆期之志，有待而行也。</li><li><strong>六五</strong>：帝乙归妹，其君之袂，不如其娣之袂良，月几望，吉。象曰：帝乙归妹，不如其娣之袂良也。其位在中，以贵行也。</li><li><strong>上六</strong>：女承筐无实，士刲羊无血，无攸利。象曰：上六无实，承虚筐也。</li></ul><h2 id="第五十五卦-丰" tabindex="-1">第五十五卦 丰 <a class="header-anchor" href="#第五十五卦-丰" aria-label="Permalink to &quot;第五十五卦 丰&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">雷火丰 震上离下</p><ul><li><strong>丰</strong>：亨，王假之，勿忧，宜日中。</li><li><strong>彖曰</strong>：丰，大也。明以动，故丰。王假之，尚大也。勿忧宜日中，宜照天下也。日中则昃，月盈则食，天地盈虚，与时消息，而况人於人乎？况於鬼神乎？</li><li><strong>象曰</strong>：雷电皆至，丰；君子以折狱致刑。</li></ul></div><ul><li><strong>初九</strong>：遇其配主，虽旬无咎，往有尚。象曰： 虽旬无咎，过旬灾也。</li><li><strong>六二</strong>：丰其蔀，日中见斗，往得疑疾，有孚发若，吉。象曰：有孚发若，信以发志也。</li><li><strong>九三</strong>：丰其沛，日中见昧，折其右肱，无咎。象曰：丰其沛，不可大事也。折其右肱，终不可用也。</li><li><strong>九四</strong>：丰其蔀，日中见斗，遇其夷主，吉。象曰：丰其蔀，位不当也。日中见斗，幽不明也。遇其夷主，吉；行也。</li><li><strong>六五</strong>：来章，有庆誉，吉。象曰：六五之吉，有庆也。</li><li><strong>上六</strong>：丰其屋，蔀其家，窥其户，阒其无人，三岁不见，凶。象曰：丰其屋，天际翔也。窥其户，阒其无人，自藏也。</li></ul><h2 id="第五十六卦-旅" tabindex="-1">第五十六卦 旅 <a class="header-anchor" href="#第五十六卦-旅" aria-label="Permalink to &quot;第五十六卦 旅&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">火山旅 离上艮下</p><ul><li><strong>旅</strong>：小亨，旅贞吉。</li><li><strong>彖曰</strong>：旅，小亨，柔得中乎外，而顺乎刚，止而丽乎明，是以小亨，旅贞吉也。旅之时义大矣哉！</li><li><strong>象曰</strong>：山上有火，旅；君子以明慎用刑，而不留狱。</li></ul></div><ul><li><strong>初六</strong>：旅琐琐，斯其所取灾。象 曰：旅琐琐，志穷灾也。</li><li><strong>六二</strong>：旅即次，怀其资，得童仆贞。象曰：得童仆贞，终无尤也。</li><li><strong>九三</strong>：旅焚其次，丧其童仆，贞厉。象曰：旅焚其次，亦以伤矣。以旅与下，其义丧也。</li><li><strong>九四</strong>：旅于处，得其资斧，我心不快。象曰：旅于处，未得位也。得其资斧，心未快也。</li><li><strong>六五</strong>：射雉一矢亡，终以誉命。象曰：终以誉命，上逮也。</li><li><strong>上九</strong>：鸟焚其巢，旅人先笑后号啕。丧牛于易，凶。象曰：以旅在上，其义焚也。丧牛于易，终莫之闻也。</li></ul><h2 id="第五十七卦-巽" tabindex="-1">第五十七卦 巽 <a class="header-anchor" href="#第五十七卦-巽" aria-label="Permalink to &quot;第五十七卦 巽&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">巽为风 巽上巽下</p><ul><li><strong>巽</strong>：小亨，利攸往，利见大人。</li><li><strong>彖曰</strong>：重巽以申命，刚巽乎中正而志行。柔皆顺乎刚，是以小亨，利有攸往，利见大人。</li><li><strong>象曰</strong>：随风，巽；君子以申命行事。</li></ul></div><ul><li><strong>初六</strong>：进退，利武人之贞。象曰：进退，志疑也。 利武人之贞，志治也。</li><li><strong>九二</strong>：巽在床下。用史巫纷若，吉无咎。象曰：纷若之吉，得中也。</li><li><strong>九三</strong>：频巽，吝。象曰：频巽之吝，志穷也。</li><li><strong>六四</strong>：悔亡，田获三品。象曰：田获三品，有功也。</li><li><strong>九五</strong>：贞吉悔亡，无不利。无初有终，先庚三日，后庚三日，吉。象曰：九五之吉，位正中也。</li><li><strong>上九</strong>：巽在床下，丧其资斧，贞凶。象曰：巽在 □ 下，上穷也。丧其资斧，正乎凶也。</li></ul><h2 id="第五十八卦-兑" tabindex="-1">第五十八卦 兑 <a class="header-anchor" href="#第五十八卦-兑" aria-label="Permalink to &quot;第五十八卦 兑&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">兑为泽 兑上兑下</p><ul><li><strong>兑</strong>：亨，利贞。</li><li><strong>彖曰</strong>：兑，说也。刚中而柔外，说以利贞，是以顺乎天，而应乎人。说以先民，民忘其劳；说以犯难，民忘其死；说之大，民劝矣哉！</li><li><strong>象曰</strong>：丽泽，兑；君子以朋友讲习。</li></ul></div><ul><li><strong>初九</strong>：和兑，吉。象曰：和兑之吉，行未疑也。</li><li><strong>九二</strong>：孚兑，吉，悔亡。象曰：孚兑之吉，信志也。</li><li><strong>六三</strong>：来兑，凶。象曰：来兑之凶，位不当也。</li><li><strong>九四</strong>：商兑，未宁，介疾有喜。象曰：九四之喜，有庆也。</li><li><strong>九五</strong>：孚于剥，有厉。象曰：孚于剥，位正当也。</li><li><strong>上六</strong>：引兑。象曰：上六引兑，未光也。</li></ul><h2 id="第五十九卦-涣" tabindex="-1">第五十九卦 涣 <a class="header-anchor" href="#第五十九卦-涣" aria-label="Permalink to &quot;第五十九卦 涣&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">风水涣 巽上坎下</p><ul><li><strong>涣</strong>：亨。王假有庙，利涉大川，利贞。</li><li><strong>彖曰</strong>：涣，亨。刚来而不穷，柔得位乎外而上同。王假有庙，王乃在中也。利涉大川，乘木有功也。</li><li><strong>象曰</strong>：风行水上，涣；先王以享于帝立庙。</li></ul></div><ul><li><strong>初六</strong>：用拯马壮，吉。象曰：初六之吉，顺 也。</li><li><strong>九二</strong>：涣奔其机，悔亡。象曰：涣奔其机，得愿也。</li><li><strong>六三</strong>：涣其躬，无悔。象曰：涣其躬，志在外也。</li><li><strong>六四</strong>：涣其群，元吉。涣有丘，匪夷所思。象曰：涣其群，元吉；光大也。</li><li><strong>九五</strong>：涣汗其大号，涣王居，无咎。象曰：王居无咎，正位也。</li><li><strong>上九</strong>：涣其血，去逖出，无咎。象曰：涣其血，远害也。</li></ul><h2 id="第六十卦-节" tabindex="-1">第六十卦 节 <a class="header-anchor" href="#第六十卦-节" aria-label="Permalink to &quot;第六十卦 节&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">水泽节 坎上兑下</p><ul><li><strong>节</strong>：亨。苦节不可贞。</li><li><strong>彖曰</strong>：节，亨，刚柔分，而刚得中。苦节不可贞，其道穷也。说以行险，当位以节，中正以通。天地节而四时成，节以制度，不伤财，不害民。</li><li><strong>象曰</strong>：泽上有水，节；君子以制数度，议德行。</li></ul></div><ul><li><strong>初九</strong>：不出户庭，无咎。象曰：不出户 庭，知通塞也。</li><li><strong>九二</strong>：不出门庭，凶。象曰：不出门庭，失时极也。</li><li><strong>六三</strong>：不节若，则嗟若，无咎。象曰：不节之嗟，又谁咎也。</li><li><strong>六四</strong>：安节，亨。象曰：安节之亨，承上道也。</li><li><strong>九五</strong>：甘节，吉；往有尚。象曰：甘节之吉，居位中也。</li><li><strong>上六</strong>：苦节，贞凶，悔亡。象曰：苦节贞凶，其道穷也。</li></ul><h2 id="第六十一卦-中孚" tabindex="-1">第六十一卦 中孚 <a class="header-anchor" href="#第六十一卦-中孚" aria-label="Permalink to &quot;第六十一卦 中孚&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">风泽中孚 巽上兑下</p><ul><li><strong>中孚</strong>：豚鱼吉，利涉大川，利贞。</li><li><strong>彖曰</strong>：中孚，柔在内而刚得中。说而巽，孚，乃化邦也。豚鱼吉，信及豚鱼也。利涉大川，乘木舟虚也。中孚以利贞，乃应乎天也。</li><li><strong>象曰</strong>：泽上有风，中孚；君子以议狱缓死。</li></ul></div><ul><li><strong>初九</strong>：虞吉，有他不燕。象曰：初九虞吉， 志未变也。</li><li><strong>九二</strong>：鸣鹤在阴，其子和之，我有好爵，吾与尔靡之。象曰：其子和之，中心愿也。</li><li><strong>六三</strong>：得敌，或鼓或罢，或泣或歌。象曰：可鼓或罢，位不当也。</li><li><strong>六四</strong>：月几望，马匹亡，无咎。象曰：马匹亡，绝类上也。</li><li><strong>九五</strong>：有孚挛如，无咎。象曰：有孚挛如，位正当也。</li><li><strong>上九</strong>：翰音登于天，贞凶。象曰：翰音登于天，何可长也。</li></ul><h2 id="第六十二卦-小过" tabindex="-1">第六十二卦 小过 <a class="header-anchor" href="#第六十二卦-小过" aria-label="Permalink to &quot;第六十二卦 小过&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">雷山小过 震上艮下</p><ul><li><strong>小过</strong>：亨，利贞，可小事，不可大事。飞鸟遗之音，不宜上宜下，大吉。</li><li><strong>彖曰</strong>：小过，小者过而亨也。过以利贞，与时行也。柔得中，是以小事吉也。刚失位而不中，是以不可大事也。有飞鸟之象焉，有飞鸟遗之音，不宜上宜下，大吉；上逆而下顺 也。</li><li><strong>象曰</strong>：山上有雷，小过；君子以行过乎恭，丧过乎哀，用过乎俭。</li></ul></div><ul><li><strong>初六</strong>：飞鸟以凶。象 曰：飞鸟以凶，不可如何也。</li><li><strong>六二</strong>：过其祖，遇其妣；不及其君，遇其臣；无咎。象曰：不及其君，臣不可过也。</li><li><strong>九三</strong>：弗过防之，从或戕之，凶。象曰：从或戕之，凶如何也。</li><li><strong>九四</strong>：无咎，弗过遇之。往厉必戒，勿用永贞。象曰：弗过遇之，位不当也。往厉必戒，终不可长也。</li><li><strong>六五</strong>：密云不雨，自我西郊，公弋取彼在穴。象曰：密云不雨，已上也。</li><li><strong>上六</strong>：弗遇过之，飞鸟离之，凶，是谓灾眚。象曰：弗遇过之，已亢也。</li></ul><h2 id="第六十三卦-既济" tabindex="-1">第六十三卦 既济 <a class="header-anchor" href="#第六十三卦-既济" aria-label="Permalink to &quot;第六十三卦 既济&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">水火既济 坎上离下</p><ul><li><strong>既济</strong>：亨，小利贞，初吉终乱。</li><li><strong>彖曰</strong>：既济，亨，小者亨也。利贞，刚柔正而位当也。初吉，柔得中也。终止则乱，其道穷也。</li><li><strong>象曰</strong>：水在火上，既济；君子以思患而预防之。</li></ul></div><ul><li><strong>初九</strong>：曳其轮，濡其尾，无咎。象曰： 曳其轮，义无咎也。</li><li><strong>六二</strong>：妇丧其茀，勿逐，七日得。象曰：七日得，以中道也。</li><li><strong>九三</strong>：高宗伐鬼方，三年克之，小人勿用。象曰：三年克之，惫也。</li><li><strong>六四</strong>：（纟需）有衣袽，终日戒。象曰：终日戒，有所疑也。</li><li><strong>九五</strong>：东邻杀牛，不如西邻之禴祭，实受其福。象曰：东邻杀牛，不如西邻之时也；实受其福，吉大来也。</li><li><strong>上六</strong>：濡其首，厉。象曰：濡其首厉，何可久也。</li></ul><h2 id="第六十四卦-未济" tabindex="-1">第六十四卦 未济 <a class="header-anchor" href="#第六十四卦-未济" aria-label="Permalink to &quot;第六十四卦 未济&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">火水未济 离上坎下</p><ul><li><strong>未济</strong>：亨，小狐汔济，濡其尾，无攸利。</li><li><strong>彖曰</strong>：未济，亨；柔得中也。小狐汔济，未出中也。濡其尾，无攸利；不续终也。虽不当位，刚柔应也。</li><li><strong>象曰</strong>：火在水上，未济；君子以慎辨物居方。</li></ul></div><ul><li><strong>初六</strong>：濡其尾，吝。象曰：濡其尾，亦不 知极也。</li><li><strong>九二</strong>：曳其轮，贞吉。象曰：九二贞吉，中以行正也。</li><li><strong>六三</strong>：未济，征凶，利涉大川。象曰：未济征凶，位不当也。</li><li><strong>九四</strong>：贞吉，悔亡，震用伐鬼方，三年有赏于大国。象曰：贞吉悔亡，志行也。</li><li><strong>六五</strong>：贞吉，无悔，君子之光，有孚，吉。象曰：君子之光，其晖吉也。</li><li><strong>上九</strong>：有孚于饮酒，无咎，濡其首，有孚失是。象曰：饮酒濡首，亦不知节也。</li></ul>',196)]))}const b=t(r,[["render",n]]);export{h as __pageData,b as default};
